        {
            _logger = logger;
            this.localizer = localizer;
        }


        [HttpGet]
        [Route("helper/lang")]
        public object Get()
        {
            var properties = localizer.GetAllStrings().Select(o => new { o.Name, o.Value });
            return properties;
        }

        // GET api/values
        [HttpGet]
        [Route("helper/hash/lang")]
        public object LangHash()
        {
            var properties = localizer.GetAllStrings().Select(o => new { o.Name, o.Value });

            string serialized = JsonConvert.SerializeObject(properties);
            string serializedLower = serialized.ToLower().Replace("\"", "").Replace("\\r\\n", "").Replace("\\", "").Replace(" ", "");
            var bytes = Encoding.UTF8.GetBytes(serializedLower);

            var r = MD5.Create().ComputeHash(bytes, 0, bytes.Length);
            var hash = BitConverter.ToString(r).Replace("-", "");
            return new { hash = hash };
        }

